        int[] i = filesTable.getSelectedRows();
        //String[] list = new String[i.length];
        List<String> list = new ArrayList<>();
        for(int j :i ){
            list.add( dir.getAbsolutePath() + "/"+ (String) filesModel.getValueAt(j, 0));
        }
